dark-mode-toggle.codeSnippet:
// Dark mode toggle
const [theme, setTheme] = useState('light');

const toggleTheme = () => {
  const newTheme = theme === 'light' ? 'dark' : 'light';
  setTheme(newTheme);
  document.documentElement.classList.toggle('dark');
};

<motion.button
  onClick={toggleTheme}
  whileHover={{ scale: 1.1 }}
  whileTap={{ scale: 0.9 }}
>
  {theme === 'light' ? <Moon /> : <Sun />}
</motion.button>

