// Shimmer effect
<div className="relative overflow-hidden bg-gray-200">
  <motion.div
    className="absolute inset-0 bg-linear-to-r from-transparent via-white/60 to-transparent"
    animate={{ x: ["-100%", "100%"] }}
    transition={{ duration: 1.5, repeat: Infinity }}
  />
</div>

